INSERT INTO `events` (`id`, `category`, `title`, `slug`, `host`, `type`, `image`, `address`, `lat`, `lon`, `pin`, `suburb`, `state`, `start_date`, `start_time`, `end_date`, `end_time`, `description`, `link`, `contact_email`, `contact_phone`, `cost`, `recurring`, `color`, `status`, `subscription_status`, `created_at`, `updated_at`) VALUES
(16, 1, 'The LavaCon Content Strategy Conference 2022', 'the-lavacon-content-strategy-conference-2022', 'Lavacon', 'online', 'uploads/event/63bd2d1ba6d4e23-01-10-09-17-15.jpg', '', NULL, NULL, '', NULL, NULL, '2022-10-23', '12:00', '2022-10-26', '12:00', '<p><span data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;LavaCon started in Hawaii (hence our name) to help organizations leverage state-of-the-art content technologies.\\n\\nHowever, LavaCon is more than just a conference. It’s a gathering place where content professionals share best practices, network with peers, and build relationships that will last for years to come.\\n\\nFrom Content Strategy to Content Marketing, our 2022 program  focuses on how content can be created and shared across the enterprise.&quot;}\" data-sheets-userformat=\"{&quot;2&quot;:769,&quot;3&quot;:{&quot;1&quot;:0},&quot;11&quot;:4,&quot;12&quot;:0}\" style=\"color: rgb(0, 0, 0); font-size: 10pt; font-family: Arial;\">LavaCon started in Hawaii (hence our name) to help organizations leverage state-of-the-art content technologies.<br><br>However, LavaCon is more than just a conference. It’s a gathering place where content professionals share best practices, network with peers, and build relationships that will last for years to come.<br><br>From Content Strategy to Content Marketing, our 2022 program focuses on how content can be created and shared across the enterprise.</span></p><p><span data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;LavaCon started in Hawaii (hence our name) to help organizations leverage state-of-the-art content technologies.\\n\\nHowever, LavaCon is more than just a conference. It’s a gathering place where content professionals share best practices, network with peers, and build relationships that will last for years to come.\\n\\nFrom Content Strategy to Content Marketing, our 2022 program  focuses on how content can be created and shared across the enterprise.&quot;}\" data-sheets-userformat=\"{&quot;2&quot;:769,&quot;3&quot;:{&quot;1&quot;:0},&quot;11&quot;:4,&quot;12&quot;:0}\" style=\"color: rgb(0, 0, 0); font-size: 10pt; font-family: Arial;\">Register By 17/09/2022,<br>Tuition is at $2350<br>Ticket after 17/09/2022 will be at $2450.<br><br>Notes:<br><br>Sessions and speakers subject to change without notice.<br>You may substitute another attendee at any time should you not be able to attend.<br>Refund requests will be issued (less a $50 processing fee) until 30 days before the event.<br>After 30 days, you can substitute another attendee or apply the tuition to a future LavaCon event.<br>We will assess a $50.00 U.S. service charge on all returned checks.<br>You understand that LavaCon will be photographing and videoing conference sessions, events, and participants. You grant us permission to do so license- and royalty-free.<br>We are on track to returning to an in-person conference. If by chance the conference is forced to go virtual, your in-person registration can be transferred to a virtual registration and the difference refunded, or your tuition can be carried over to next year, LavaCon 2023 in San Diego.</span></p>', 'https://lavacon.org/', '', '', 0.00, '', NULL, 1, 7, '2023-01-10 09:17:15', '2023-01-17 06:25:27'),
(17, 8, 'CopyCon 2022 9th Annual Conference', 'copycon-2022-9th-annual-conference', 'ProCopywriters', 'online', 'uploads/event/63c11f50ad4c023-01-13-09-07-28.jpg', '', NULL, NULL, '', NULL, NULL, '2022-10-14', '08:30', '2022-10-14', '14:10', '<p><span data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;Save with your\\nProCopywriters discount.\\nSave 40% on your tickets with a ProCopywriters Pro or Corporate membership. \\n\\nJoin &amp; Save\\nPro and Corporate members of ProCopywriters save up to £125 on Conference Day and Training Day tickets.\\nHere’s how to claim your discount\\n\\n1. Sign in to your ProCopywriters dashboard and then scroll down to see the offers.\\n2. Copy the discount code\\n3. Return to the CopyCon website\\n4. Choose your full-price ticket for the Conference, Training or Video package\\n5. Enter the relevant discount code&quot;}\" data-sheets-userformat=\"{&quot;2&quot;:1049409,&quot;3&quot;:{&quot;1&quot;:0},&quot;9&quot;:0,&quot;11&quot;:3,&quot;12&quot;:0,&quot;23&quot;:1}\" data-sheets-textstyleruns=\"{&quot;1&quot;:0}{&quot;1&quot;:72,&quot;2&quot;:{&quot;2&quot;:{&quot;1&quot;:2,&quot;2&quot;:1136076},&quot;9&quot;:1}}{&quot;1&quot;:114}\" data-sheets-hyperlinkruns=\"{&quot;1&quot;:72,&quot;2&quot;:&quot;http://www.procopywriters.co.uk/join/&quot;}{&quot;1&quot;:114}\" style=\"color: rgb(0, 0, 0); font-size: 10pt; font-family: Arial;\"><span style=\"font-size: 10pt;\">Save with your<br>ProCopywriters discount.<br>Save 40% on your tickets with a </span><span style=\"font-size: 10pt; text-decoration-line: underline; text-decoration-skip-ink: none; color: rgb(17, 85, 204);\"><a class=\"in-cell-link\" target=\"_blank\" href=\"http://www.procopywriters.co.uk/join/\">ProCopywriters Pro or Corporate membership</a></span><span style=\"font-size: 10pt;\">.<br><br>Join &amp; Save<br>Pro and Corporate members of ProCopywriters save up to £125 on Conference Day and Training Day tickets.<br>Here’s how to claim your discount<br><br>1. Sign in to your ProCopywriters dashboard and then scroll down to see the offers.<br>2. Copy the discount code<br>3. Return to the CopyCon website<br>4. Choose your full-price ticket for the Conference, Training or Video package<br>5. Enter the relevant discount code</span></span></p>', 'https://www.copywritingconference.com/conference/', '', '', 43.00, '', NULL, 1, 7, '2023-01-13 09:07:28', '2023-01-13 09:07:28'),
(20, 5, 'WORKBENCHCON The Conference For Makers & Content Creators', 'workbenchcon-the-conference-for-makers-content-creators', 'WORKBENCHCON', 'in person', 'uploads/event/63c64e6f8ea2123-01-17-07-29-51.jpg', 'Renaissance Atlanta Waverly Hotel & Convention Center 2450 Galleria Parkway, Atlanta GA 30339', NULL, NULL, '30339', NULL, NULL, '2023-03-02', '16:00', '2023-03-04', '18:00', '<p><span data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;Be a part of the growing network of diyers, makers, influencers &amp; bloggers discovering new techniques, products and skills all while learning how to take their businesses to the next level.\\nWhat to Expect At WORKBENCHcon\\n2.5 days full of energy that will ignite your business success all year long! Networking with both peers and brands at WBC2022 social events and during meals will bring you so much opportunity….\\n\\nYou will learn new techniques using tools and new equipment, skills for video production, business practices for the content creator, and more!&quot;}\" data-sheets-userformat=\"{&quot;2&quot;:769,&quot;3&quot;:{&quot;1&quot;:0},&quot;11&quot;:4,&quot;12&quot;:0}\" style=\"color: rgb(0, 0, 0); font-size: 10pt; font-family: Arial;\">Be a part of the growing network of DIYers, makers, influencers &amp; bloggers discovering new techniques, products and skills all while learning how to take their businesses to the next level.</span></p><p><span data-sheets-value=\"{&quot;1&quot;:2,&quot;2&quot;:&quot;Be a part of the growing network of diyers, makers, influencers &amp; bloggers discovering new techniques, products and skills all while learning how to take their businesses to the next level.\\nWhat to Expect At WORKBENCHcon\\n2.5 days full of energy that will ignite your business success all year long! Networking with both peers and brands at WBC2022 social events and during meals will bring you so much opportunity….\\n\\nYou will learn new techniques using tools and new equipment, skills for video producti