(1, 'Lorem ipsum dolor sit?', 'lorem-ipsum-dolor-sit-2', 'uploads/job/6357de040831422-10-25-01-00-52.png', 3, 'parttime', NULL, '<p><span style=\"color: rgb(32, 33, 36); font-family: consolas, &quot;lucida console&quot;, &quot;courier new&quot;, monospace; font-size: 12px; white-space: pre-wrap; background-color: rgba(0, 0, 0, 0.05);\"> Lorem ipsum dolor sit amet consectetur adipisicing elit. Consequatur vitae necessitatibus optio. Quos laborum voluptatum libero cumque alias accusantium\r\n asperiores!</span></p>', 'kolkata', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'indeed', 'month', '1000', NULL, NULL, '2022-10-12', '2022-10-27', 'social media', 1, 1, 1, '2022-10-25', '2022-11-13', NULL, NULL, NULL, NULL, NULL, 'https://www.lipsum.com', NULL),
(2, 'Corporate Creative Liaison', 'corporate-creative-liaison-2', NULL, 8, 'remote', 'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.', '<p>sfdsd fsdf sd&nbsp;</p>', NULL, '689 Achyut Forest', 74, 'Gurdaspur', 'Madya Pradesh', 'Afghanistan', 'your.email+fakedata73546@gmail.com', 'Pondicherr', 'Quibusdam sed dicta minus.', NULL, 'Problogger', 'hour', '99', NULL, NULL, '2023-09-21', '2023-09-15', 'tag1, tag 2', 1, 0, 1, '2022-11-03', '2022-11-03', NULL, NULL, NULL, NULL, NULL, 'https://www.lipsum.com', NULL),
(3, 'Senior Corporate Accountant', 'senior-corporate-accountant-2', NULL, 3, 'fulltime', 'The Senior Corporate Accountant is a full cycle accounting role responsible for analyzing and reviewing balance sheets, intercompany accounting, preparation of financial results, inventory accounting, technical accounting, supporting payroll, SOX.', '<p><span style=\"color: rgb(68, 68, 68); font-family: halcyon-regular, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\">The Senior Corporate Accountant is a full cycle accounting role responsible for analyzing and reviewing balance sheets, intercompany accounting, preparation of financial results, inventory accounting, technical accounting, supporting payroll, SOX.</span></p>', NULL, '21', 2000, 'Liverpool', 'NSW', 'India', 'H.S pass', 'writer', '1', NULL, 'indeed', 'month', '20000', NULL, NULL, '2022-11-12', '2022-12-16', '', 1, 0, 0, '2022-11-12', '2022-11-12', NULL, NULL, NULL, NULL, NULL, 'https://www.lipsum.com', NULL),
(4, 'Content Writer', 'content-writer', NULL, 3, 'fulltime', 'Conducting in-depth research on industry-related topics in order to develop original content.\r\nDeveloping content for blogs, articles, product descriptions, social media, and the company website.\r\nWrite original copy for ads and social networks\r\nCreating & developing ad copies for Google, Instagram, Facebook, and other marketing campaigns.\r\nPrepare and publish articles that describe our company and products\r\nAssisting the marketing team in developing content for advertising campaigns.\r\nProofreading content for errors and inconsistencies.\r\nEditing and polishing existing content to improve readability.\r\nConducting keyword research and using SEO best practices to increase traffic to the company website.\r\nCreating compelling headlines and body copy that will capture the attention of the target audience.\r\nIdentifying customers’ needs and recommending new content to address gaps in the company\'s current content.', '<p>Conducting in-depth research on industry-related topics in order to develop original content.</p><p>Developing content for blogs, articles, product descriptions, social media, and the company website.</p><p>Write original copy for ads and social networks</p><p>Creating &amp; developing ad copies for Google, Instagram, Facebook, and other marketing campaigns.</p><p>Prepare and publish articles that describe our company and products</p><p>Assisting the marketing team in developing content for advertising campaigns.</p><p>Proofreading content for errors and inconsistencies.</p><p>Editing and polishing existing content to improve readability.</p><p>Conducting keyword research and using SEO best practices to increase traffic to the company website.</p><p>Creating compelling headlines and body copy that will capture the attention of the target audience.</p><p>Identifying customers’ needs and recommending new content to address gaps in the company\'s current content.</p>', NULL, '11', 2541, 'ultimo', 'NSW', 'India', 'Graduate', 'Creating compelling headlines and body copy that will capture the attention of the target audience.', '5', NULL, 'indeed', 'month', '25000', NULL, NULL, '2022-11-12', '2023-01-12', '', 1, 1, 0, '2022-11-12', '2022-11-12', NULL, NULL, NULL, NULL, NULL, 'https://www.lipsum.com', NULL),
