INSERT INTO `market_types_banners` (`id`, `market_type_id`, `content_heading`, `content`, `content_btn`, `content_btn_link`, `image`, `created_at`, `updated_at`) VALUES
(4, '1', 'Expert content in all blog niches', '<p><span id=\"docs-internal-guid-4b5f1615-7fff-0fba-bd29-a4c4d92573d4\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">We have talented blog writers in digital marketing, personal finance, recipes and food, parenting, health and fitness, self-care and wellbeing, making money online, blogging, and personal development, to name a few. Access new viewpoints on your niche today</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7e43a4ae5123-01-18-12-21-14.png', '2023-01-18 12:21:14', '2023-01-18 12:21:14'),
(5, '1', 'Your search is over', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">You need to keep up with the latest niche trends - imperative to boosting visitor numbers and click-through rates. With [product name], you access a database of pre-written, edited, and optimized blog content from professional writers specializing in various niches.</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span id=\"docs-internal-guid-3d68a542-7fff-93ba-b984-5b12dc3a3c66\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Focus on building passive income streams instead of laboriously researching trends and writing fresh blog posts.</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7e4f1d108a23-01-18-12-24-17.png', '2023-01-18 12:24:17', '2023-01-30 13:14:41'),
(6, '1', 'The solution to your blogging woes', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Join our database of savvy blog owners where talented writers can submit content on spec.&nbsp;</span></p><p><span id=\"docs-internal-guid-096e3848-7fff-063d-912f-0e373baf82bc\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">The result? You get quality blog posts written by niche experts without the risk of paying for something that might not be right for your blog - no need to pay upfront!</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7e53192aac23-01-18-12-25-21.png', '2023-01-18 12:25:21', '2023-01-18 12:25:21'),
(7, '2', 'Your search is over', '<p><span id=\"docs-internal-guid-5583bd8f-7fff-d35d-3418-c3b3331e7082\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span id=\"docs-internal-guid-0a91e308-7fff-77ef-a085-ccc235c92ea9\"><span style=\"font-size: 12pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">You\'re looking for a writer but don\'t have time to search for the best one. [product name] will find you top-notch writers who can do the job. Whether you need articles, blogs, web copy, or emails, we\'ve got you covered.</span></span><br></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7eb24c447923-01-18-12-50-44.png', '2023-01-18 12:50:44', '2023-01-30 13:50:49'),
(8, '2', 'Quality content that won’t break the bank', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Writing high-quality content is key to growing your business. But we understand that money can be tight. We\'ll help you find premium content writers and copywriters without the premium price.</span></p><p><span id=\"docs-internal-guid-17f9922a-7fff-9d8d-3a75-76abcf9f34e2\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">So what are you waiting for? Boost your ROI by signing up today!</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7eb767ec8623-01-18-12-52-06.png', '2023-01-18 12:52:06', '2023-01-18 12:52:06'),
(9, '2', 'Get your audience excited about your business', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">When you hire from [product name], you\'ll find someone who can help you create content that attracts customers and builds relationships with them so they keep coming back to buy from you again and again.</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span id=\"docs-internal-guid-09e28a48-7fff-22f8-dc45-dba1182d0846\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">The result? Higher profits for everyone!</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7eb9a517e723-01-18-12-52-42.png', '2023-01-18 12:52:42', '2023-01-30 13:51:23'),
(10, '3', 'All the tools and training you need to succeed', '<p><span id=\"docs-internal-guid-c42be774-7fff-3d0f-f5bd-92f71342478c\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">We\'ve been helping many writers like you find their way into their dream job. Whether a newbie or an experienced pro, we can help you build a business from scratch and launch your career as a freelance writer.</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7f03ef2eff23-01-18-01-12-30.png', '2023-01-18 13:12:30', '2023-01-18 13:12:30'),
(11, '3', 'For new and pro writers', '<p><span id=\"docs-internal-guid-b08a4a17-7fff-b49e-6823-5b9749ee465c\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">No matter what stage of your career, we\'ve got you covered. Whether you\'re a new writer looking for ways to get paid for your content or an experienced freelance writer looking for more clients, {name of product} makes it easy for anyone to take their writing career to the next level.</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span id=\"docs-internal-guid-f279dccf-7fff-edb2-d8b6-516dc59a1c70\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">So what are you waiting for? Boost your income by signing up today!</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7f0620cf9223-01-18-01-13-06.png', '2023-01-18 13:13:06', '2023-01-30 13:55:27'),
(12, '3', 'Time to stop dreaming and start doing', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">You\'ve tried to start your freelance writing career, but you can\'t seem to get off the ground. You have no idea how to find clients or where to start when it comes to setting up a business. You\'re overwhelmed by all the options and don\'t know which ones are best for you.&nbsp;</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span id=\"docs-internal-guid-3d71ed84-7fff-4744-5a24-a43b74876f01\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">We’ve got you covered. [product name] is the all-in-one solution to finding and retaining clients, setting up a successful writing business, and living the life you deserve.</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7f08abfd1123-01-18-01-13-46.png', '2023-01-18 13:13:46', '2023-01-30 13:55:50'),
(13, '4', 'Expert content in all industries', '<p><span id=\"docs-internal-guid-f669146a-7fff-2c9a-b8bb-0a9f071fc625\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">We have talented writers in SaaS, real estate, finance, eCommerce, fintech, recruitment, marketing, hospitality, insurance, automotive, services, health, retail, tech, and legal, to name a few. Access new viewpoints on your industry today.</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7f96f6cc7e23-01-18-01-51-43.png', '2023-01-18 13:51:43', '2023-01-18 13:51:43'),
(14, '4', 'Your search is over', '<p>You need to keep up with the latest industry trends - key to boosting your visibility and generating leads. With [product name], you access a database of pre-written, edited and optimized content from professional writers specializing in various industries.\r\n</p><p>Focus on sales and profits instead of laboriously researching trends and writing fresh content.\r\n</p>', 'Start Now', NULL, 'uploads/marketbanner/63c7f9995561323-01-18-01-52-25.png', '2023-01-18 13:52:25', '2023-01-30 14:05:24'),
(15, '4', 'The solution to your content woes', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Join our database of savvy businesses and agencies where talented writers can submit content on spec.&nbsp;</span></p><p><span id=\"docs-internal-guid-10ecd14d-7fff-32f8-36c1-839ba30550bd\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">The result? You get quality content written by industry experts without the risk of paying for something that might not be right for your business needs - no need to pay upfront!</span></p>', 'Start Now', NULL, 'uploads/marketbanner/63c7f9b2f309c23-01-18-01-52-50.png', '2023-01-18 13:52:50', '2023-01-18 13:52:50');

