INSERT INTO `market_types_categories` (`id`, `market_type_id`, `title`, `slug`, `image`, `category_description_heading`, `category_description`, `category_description_image`, `category_description_btn`, `category_description_btn_link`, `status`, `created_at`, `updated_at`) VALUES
(1, '1', 'Blog Visitors', 'for-enterprise', 'uploads/marketcategories/63c7f2e40a3ea23-01-18-01-23-48.png', 'Increase the number of visitors to your blog', '<span id=\"docs-internal-guid-d7ef9299-7fff-b5d1-843e-27984ffc982d\"><span style=\"font-size: 12pt; font-family: Arial; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Keep your blog active with a steady stream of fresh content from a talent pool with niche-specific knowledge. </span><span style=\"font-size: 12pt; font-family: Arial; color: rgb(0, 0, 0); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Demonstrate to your audience that your blog is the premier place to find the latest news.</span></span>', 'uploads/marketcategoriescontent/636e589a1ede222-11-11-02-13-46.png', 'Find Blog Content Now', NULL, 1, '2022-09-14 11:36:44', '2023-01-18 13:23:48'),
(2, '1', 'Affiliate Marketing', 'website-content', 'uploads/marketcategories/636e591141e0722-11-11-02-15-45.png', 'Boost affiliate marketing sales', '<p><span id=\"docs-internal-guid-3d719946-7fff-f5ff-8b8e-bc51bc3ca750\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Find writers on our database who understand SEO-friendly strategies, such as strategic keyword placement, meta tags &amp; descriptions, and formatting with headers and bullets. The resulting increase in blog visitors means more eyeballs on your affiliate offers. Boost affiliate sales without writing any content yourself!</span></p>', 'uploads/marketcategoriescontent/636e59114200f22-11-11-02-15-45.png', 'Find Blog Content Now', NULL, 1, '2022-09-14 11:46:32', '2023-01-18 12:35:38'),
(3, '1', 'Ads / PPC Revenue', 'blog-posts', 'uploads/marketcategories/636e583dd7ef822-11-11-02-12-13.png', 'Make more money with direct ads, PPC, and CPC', 'How much ad revenue you generate depends on your blog\'s popularity. Choose relevant content from our database or receive article submissions from a talented pool of freelancer writers to increase blog visitors and click-through rates - no need for tedious research and writing.', 'uploads/marketcategoriescontent/63c7e7ca9822e23-01-18-12-36-26.png', 'Find Blog Content Now', NULL, 1, '2022-09-14 11:47:38', '2023-01-18 12:36:26'),
