(5, '1', 'Sell Products', 'for-publishers', 'uploads/marketcategories/636e58bf1e32422-11-11-02-14-23.png', 'Boost sales of your own products', '<p><span id=\"docs-internal-guid-d2641994-7fff-cf4d-be5f-f0212fdb735b\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:12pt;margin-bottom:12pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Whether you sell digital or physical products, either yourself or via dropshipping, you’ll enjoy a big increase in sales with expert content that attracts more visitors to your site.</span></p>', 'uploads/marketcategoriescontent/636e58bf1e53e22-11-11-02-14-23.png', 'Find Blog Content Now', NULL, 1, '2022-09-14 11:50:19', '2023-01-18 12:37:07'),
(6, '1', 'Premium Content', 'script-writing', 'uploads/marketcategories/636e58e8bd07822-11-11-02-15-04.png', 'Generate multiple income streams', '<p><span id=\"docs-internal-guid-422adacb-7fff-b9fa-443d-fa7f1cd986e7\"></span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:17pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">With a continual supply of engaging, niche-specific content, your blog turns into an authority site. Monetize further using paywalls, membership sites, and subscription newsletters. You can create so many streams of income when you’re not distracted by endless blogging.</span></p>', 'uploads/marketcategoriescontent/636e58e8bd1fa22-11-11-02-15-04.png', 'Find Blog Content Now', NULL, 1, '2022-09-14 11:52:02', '2023-01-18 12:37:20'),
