(1, 1, 'Regional, St. Croix Valley MN and WI, Lifestyle, Culture, Art, Entertainment, Food', 'regional-st-croix-valley-mn-and-wi-lifestyle-culture-art-entertainment-food', 2, NULL, 'St. Croix Valley Magazine', 'Feature-rich and stylish, St. Croix Valley Magazine emphasizes the best the communities of the St. Croix Valley have to offer. With insider information and engaging articles about local dining, shopping and leisure activities, this monthly publication highlights local residents with stories to tell. Our coverage is fresh and friendly with a commitment to living in and enjoying the St. Croix Valley community.', '', 'julie.pfitzinger@tigeroak.com', '', '', '', '', '2023-01-31 08:21:20', '2023-01-31 08:21:20'),
(2, 8, 'How to Write a Blog Post', 'how-to-write-a-blog-post', 0, NULL, 'Hubspot blog', 'Review a step-by-step guide plus useful templates to learn how to write an effective blog post for your target audience and customers.', 'https://blog.hubspot.com/marketing/how-to-start-a-blog', '', '', '', '', 'uploads/pitch_blogs/63dcc916ef76823-02-03-08-43-02.jpeg', '2023-02-03 08:43:02', '2023-02-03 08:43:02'),
(3, 8, 'How to Write a Blog Post', 'how-to-write-a-blog-post-2', 3, NULL, 'Hubspot', 'Review a step-by-step guide plus useful templates to learn how to write an effective blog post for your target audience and customers.', '', '', '', '', '', 'uploads/pitch_blogs/63dcc97fd8d5623-02-03-08-44-47.jpeg', '2023-02-03 08:44:47', '2023-02-03 08:44:47'),
(4, 1, 'Earum eu ea harum qu', 'earum-eu-ea-harum-qu', 2, NULL, 'Commodi minima quo d', 'Sequi est qui eiusmo', 'Quas dolor cumque eu', 'dabidy@mailinator.com', 'Facilis minus offici', 'Voluptatibus dolorib', 'Iste quia explicabo', 'uploads/pitch_blogs/63dccf2c1668923-02-03-09-09-00.png', '2023-02-03 09:09:00', '2023-02-03 09:09:00'),
(5, 7, 'GENERAL SAVINGS TIPS', 'general-savings-tips-2', 4, NULL, 'America Saves', 'If you’ve taken the America Saves pledge, you’ve already chosen a savings goal which means you’re ahead of the savings curve!', 'https://americasaves.org/resource-center/insights/54-ways-to-save-money/', 'test12@yopmail.com', '4564564562', 'Test', '', 'uploads/pitch_blogs/63dcd1353df6923-02-03-09-17-41.jpeg', '2023-02-03 09:17:41', '2023-02-03 09:20:25'),
(6, 7, 'Start Small. Think Big', 'start-small-think-big', 5, NULL, 'America Saves', 'Save automatically. Setting up automatic savings is the easiest and most effective way to save, and it puts extra cash out of sight and out of mind. Automatic savings means you have a process in place to save at regular intervals, whether that’s monthly, weekly, or daily.', 'https://americasaves.org/resource-center/insights/54-ways-to-save-money/', 'americasaves@gmail.com', '', '', '', 'uploads/pitch_blogs/63dcd193ba09123-02-03-09-19-15.jpg', '2023-02-03 09:19:15', '2023-02-03 09:19:15'),
(8, 1, 'Test Blog', 'test-blog', 2, NULL, 'Google', 'Website Description (optional)Website Description (optional)', 'https://www.google.co.in/', 'test12@email.com', '7845784585', '2200', '', 'uploads/pitch_blogs/63dd15b2372fb23-02-03-02-09-54.jpeg', '2023-02-03 14:09:54', '2023-02-03 14:09:54');

